books = [gobleOfFire, orderOfThePhoenix]

letterCount = [[chr(i) for i in range(ord('a'), ord('z') + 1)], [0 for i in range(26)]]

for book in books:
    for char in book:
        for i in range(len(letterCount[0])):
            if char == letterCount[0][i]:
                letterCount[1][i] += 1

for i in range(len(letterCount[0])):
    print(letterCount[0][i] + ": " + str(letterCount[1][i]))

(memory, peak) = tracemalloc.get_traced_memory()

print("--- %s seconds ---" % round((time.time() - start_time), 2))
print(f"Memory usage is {memory / 10**3}KB; Peak was {peak / 10**3}KB")

tracemalloc.stop()